mapper.select:
SELECT le.* , lmanager.lat_company as lat_company, lmanager.lng_company as lng_company, lmanager.company_address as company_address, lmanager.company_person as company_person, lmanager.company_tel as company_tel, lmanager.company_detail as company_detail, lmanager.company_name as company_name, lmanager.yingye_time_start as yingye_time_start, lmanager.yingye_time_end as yingye_time_end, lmanager.shouhui as shouhui, lmanager.company_pic as company_pic
-- lad.mm_ad_title as mm_ad_title, -- lad.mm_ad_pic as mm_ad_pic, -- lad.mm_ad_url as mm_ad_url, -- -- lthree.pingtai_url as pingtai_url, -- -- lthr.school_three_pingtai_pic as school_three_pingtai_pic, -- lthr.school_three_pingtai_name as school_three_pingtai_name
from lx_emp le LEFT JOIN manager_info lmanager ON lmanager.emp_id = le.emp_id -- LEFT JOIN seller_ad lad ON lad.emp_id = le.emp_id -- LEFT JOIN emp_pingtai_mng lthree ON lthree.emp_id = le.emp_id -- LEFT JOIN school_three_pingtai lthr ON lthree.school_three_pingtai_id = lthr.school_three_pingtai_id WHERE le.emp_typeid = '2'
and le.school_id=#{school_id}
and le.emp_id=#{emp_id}
AND le.emp_name like CONCAT('%',#{keyWords},'%')
limit #{index},#{size}

SELECT * FROM LX_EMP le
LEFT JOIN SCHOOL_RECORD_COMMENT src
ON src.COMMENT_EMP_ID = le.EMP_ID WHERE src.COMMENT_ID=#{fplId}

  SELECT le.*, ls.name as school_name
    from lx_emp le
  left join college ls on le.school_id=ls.coid
  WHERE 1=1

        and le.school_id=#{schoolId}


        AND le.emp_mobile=#{phoneNumber}



        AND le.school_id=#{school_id}



        AND le.emp_name like CONCAT('%',#{keyWords},'%')

ORDER BY le.dateline DESC
limit #{index},#{size}

select count(le.school_id) from lx_emp le left join college ls on le.school_id=ls.coid
WHERE 1=1

    and le.school_id=#{schoolId}


    AND le.emp_mobile=#{phoneNumber}

select * from lx_emp where '1'='1'

 AND emp_mobile=#{keyWords} or emp_name like CONCAT('%',#{keyWords},'%')

limit #{index},#{size}
