mapper.select:
SELECT DISTINCT sg.emp_id,sg.SCHOOL_ID,sg.CONTRACT_ID, le.emp_name, le.emp_cover, le.EMP_MOBILE,co.NAME as school_name, sg.END_TIME FROM Seller_goods sg LEFT JOIN lx_emp le on sg.emp_id=le.emp_id LEFT JOIN college co on sg.school_id=co.coid WHERE sg.contract_id=#{empId} and le.emp_id is NOT NULL
and le.emp_mobile=#{phone}
and le.emp_name like CONCAT('%',#{name},'%')
limit #{index},#{size}

SELECT count(sg.ID) FROM SELLER_GOODS sg LEFT JOIN lx_emp le on sg.emp_id=le.emp_id WHERE sg.CONTRACT_ID=#{empId} and le.emp_id is NOT NULL
and le.emp_mobile=#{phone}
and le.emp_name like CONCAT('%',#{name},'%')

SELECT DISTINCT le.EMP_NAME, le.EMP_MOBILE, sg.EMP_ID, sg.CONTRACT_ID,a.total_money, a.is_account FROM SELLER_GOODS sg LEFT JOIN ( SELECT SELLER_EMP_ID,sum(PAYABLE_AMOUNT) total_money, sum(ISACCOUNT) is_account FROM PAOPAO_SHOPPING_ORDER WHERE COMPLETION_TIME >#{startTime} AND COMPLETION_TIME <#{endTime} AND PAY_STATUS='1' GROUP BY SELLER_EMP_ID ) a ON sg.EMP_ID = a.SELLER_EMP_ID LEFT JOIN LX_EMP le ON sg.EMP_ID=le.EMP_ID WHERE '1'='1'
and a.is_account=#{isAccount}
and a.is_account !='0'
limit #{index},#{size}
