mapper.select:
SELECT m.* FROM ( SELECT sr.*, (select count(paopao_video_id) from paopao_video_favour zan where zan.paopao_video_id = sr.paopao_video_id) as zan_num, (select count(paopao_video_id) from paopao_video_comment pl where pl.paopao_video_id = sr.paopao_video_id) as pl_num FROM paopao_video sr WHERE 1='1'
AND paopao_isdel=#{isdel}
) m
ORDER BY m.dateline DESC
limit #{index},#{size}

SELECT m.* FROM ( SELECT sr.*, (select count(paopao_video_id) from paopao_video_favour zan where zan.paopao_video_id = sr.paopao_video_id) as zan_num, (select count(paopao_video_id) from paopao_video_comment pl where pl.paopao_video_id = sr.paopao_video_id) as pl_num FROM paopao_video sr WHERE 1='1'
AND paopao_isdel=#{isdel}
) m
ORDER BY m.zan_num DESC
limit #{index},#{size}
