mapper.select:
SELECT pz.*, le.emp_name, le.emp_cover,cl.name as school_name, (select count(PK_ZP_ID) from PK_ZAN zan where zan.PK_ZP_ID = pz.ZP_ID) as zan_num, (select count(PK_ZP_ID) from PK_COMMENT pl where pl.PK_ZP_ID = pz.ZP_ID) as pl_num FROM PK_ZP pz LEFT JOIN lx_emp le ON pz.EMP_ID=le.emp_id LEFT JOIN college cl on pz.SCHOOL_ID=cl.coid LEFT JOIN PK_THEME pt on pz.ZT_ID = pt.PK_ID WHERE pz.ZP_ISUSE='0' AND le.is_use='0'
AND pt.IS_USE='1'
AND pz.ZT_ID=#{themeId} AND pt.IS_USE='2'
AND pz.SCHOOL_ID=#{schoolId}
AND pz.emp_id=#{empId}
AND le.EMP_NAME like CONCAT('%',#{keyWords},'%')
ORDER BY zan_num DESC
ORDER BY pz.dateline DESC
limit #{index},#{size}

SELECT pz.*, le.emp_name, le.emp_cover,cl.name as school_name, (select count(PK_ZP_ID) from PK_ZAN zan where zan.PK_ZP_ID = pz.ZP_ID) as zan_num, (select count(PK_ZP_ID) from PK_COMMENT pl where pl.PK_ZP_ID = pz.ZP_ID) as pl_num FROM PK_ZP pz LEFT JOIN lx_emp le ON pz.EMP_ID=le.emp_id LEFT JOIN college cl on pz.SCHOOL_ID=cl.coid LEFT JOIN PK_THEME pt on pz.ZT_ID = pt.PK_ID WHERE pz.ZP_ISUSE='0' AND le.is_use='0' AND pt.IS_USE='1'
AND pz.SCHOOL_ID=#{schoolId}
AND le.EMP_NAME like CONCAT('%',#{keyWords},'%')
ORDER BY zan_num DESC
ORDER BY pz.dateline DESC
limit #{index},#{size}
